BEGIN TRANSACTION;
CREATE TABLE gifts (
	id INTEGER NOT NULL, 
	name VARCHAR(80) NOT NULL, 
	"desc" VARCHAR(250), 
	link VARCHAR(250), 
	image VARCHAR(250), 
	status VARCHAR(10), 
	date_added DATE NOT NULL, 
	date_given DATE, 
	giver_id INTEGER, 
	rec_id INTEGER, 
	PRIMARY KEY (id), 
	FOREIGN KEY(giver_id) REFERENCES givers (id), 
	FOREIGN KEY(rec_id) REFERENCES recipients (id)
);
COMMIT;
